import React, { ChangeEvent, useState } from 'react';
import { InputBarStyled, InputText } from './styles';
const Fade = require('react-reveal/Fade');
const Pulse = require('react-reveal/Pulse');


interface IInputBarProps {
    text: string;
    placeholder: string;
    onKeyPress: (e: React.KeyboardEvent<HTMLInputElement>) => void;
    onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
    showText: boolean;
    pulseText: boolean;
  }


  const InputBar: React.FC<IInputBarProps> = (props) => {


    return (
      <InputBarStyled>
      <Fade top opposite collapse when={props.showText}>
        <Pulse when={props.pulseText}>
        <InputText value={props.text} />
        <InputText
          type='text'
          placeholder={props.placeholder}
          value={props.text}
          onChange={props.onChange}
          onKeyPress={props.onKeyPress}
        />
        </Pulse>
        </Fade>


      </InputBarStyled>
    );
  };

  export default InputBar;
