import React, { ChangeEvent, useState } from 'react';
import { InputBarStyled, InputText, IntroStyled, IntroStyledText } from './styles';
import Typed from 'react-typed';
const Fade = require('react-reveal/Fade');





  const Intro: React.FC = () => {
    const [showText, setShowText] = useState(true);
    const [welcomeString, setWelcomeString] = useState('welcome');
    const [count, setCount] = useState(0);

    const welcomeStrings = [
        'welcome to digital detox',
        'how are you'
    ]

    const componentDidMount = () => {
        onTimer();
    }
    const onTimer = () => {
        setTimeout(() => {
            setShowText(false);
            setWelcomeString(welcomeStrings[count]);
            setCount(count+1);
            onTimer();
        }, 100);
    }

    return (
        <>
      <IntroStyled>
      <Typed
        strings={welcomeStrings}
            typeSpeed={600}
            fadeOut={true}
        />
      </IntroStyled>
      </>
    );
  };

  export default Intro;
