import React, { useState } from 'react';
import './App.css';
import InputBar from './InputBar';
import Typed from 'typed.js';




const App: React.FC = () => {
  const [inputText, setInputText] = useState('');
  let strings_ray = [];

  const options = {
    strings: [''],
    typeSpeed: 50,
    backSpeed: 50,
    fadeOut: true,
  };




  const send_message = () => {

  }

  const fade = () => {
    var typed = new Typed(strings_ray, options);
  }

  const onKeyPress = async (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      send_message();
      setInputText('');
    }

    else if(e.key == 'Space') {
      fade();
    }

    strings_ray.push(e.key);

  }

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInputText(e.target.value);
    //fade();
  }


  return (
    <div className="App">
      <header className="App-header">
        <InputBar text={inputText} placeholder="got milk?" onKeyPress={onKeyPress} onChange={onChange}/>
      </header>
    </div>
  );
}

export default App;
