import React, { useState } from 'react';
import './App.css';
import InputBar from './InputBar';
import Typed from 'react-typed';




const App: React.FC = () => {
  const [inputText, setInputText] = useState('');
  const [fadeText, setFadeText] = useState('');



  const send_message = () => {

  }

  const fade = () => {

  }

  const onKeyPress = async (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      send_message();
      setInputText('');
    }

    else if (e.key == 'Space') {
      fade();
      setFadeText('');
    }


  }

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInputText(e.target.value);
    setFadeText(e.target.value);
    //fade();
  }


  return (
    <div className="App">
      <header className="App-header">
        <InputBar text={inputText} placeholder="got milk?" onKeyPress={onKeyPress} onChange={onChange} />
        <Typed
          strings={[fadeText]}
          typeSpeed={100}
          //backSpeed={50}
          fadeOut={true} 
          loop >

        </Typed>
      </header>
    </div>
  );
}

export default App;
