  const [inputText, setInputText] = useState('');
  const [fadeText, setFadeText] = useState('');
  const [showText, setShowText] = useState(true);

  const send_message = () => {

  }

  const fade = () => {
    //backspace?
  }

  const onKeyPress = async (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      send_message();
      setTimeout(() =>
        {setInputText('')}, 
        500);

      setFadeText('');
      setShowText(false);
      setTimeout(() =>
        {setShowText(true)}, 
        1500);

    }

    else if (e.key == ' ') {
      fade();
      //setFadeText('');
      //setShowTyped(true);

    }


  }

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {

    setInputText(e.target.value);
    setFadeText(e.target.value);

    //fade();
  }


  return (
    <div className="App">
      <header className="App-header">
        <InputBar text={inputText} placeholder="type your thoughts here" onKeyPress={onKeyPress} onChange={onChange} showText={showText}/>
        <p>and press enter to release them</p>


      </header>
    </div>
  );
}

export default App;
