import React, { useState } from 'react';
import './App.css';
import InputBar from './InputBar';
import Typed from 'react-typed';



const App: React.FC = () => {
  const [inputText, setInputText] = useState('');
  const [fadeText, setFadeText] = useState('');
  const [showTyped, setShowTyped] = useState(false);

  const send_message = () => {

  }

  const fade = () => {
    //backspace?
  }

  const onKeyPress = async (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      send_message();
      setTimeout(() =>
        {setInputText('')}, 
        2000);

      setFadeText('');

    }

    else if (e.key == ' ') {
      fade();
      //setFadeText('');
      //setShowTyped(true);

    }


  }

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {

    setInputText(e.target.value);
    setFadeText(e.target.value);

    //fade();
  }


  return (
    <div className="App">
      <header className="App-header">
        <Fade>
        <InputBar text={inputText} placeholder="type your thoughts here" onKeyPress={onKeyPress} onChange={onChange} />
        </Fade>
        <p>and press enter to release them</p>


        {showTyped ? 
          <div id="typed-div" >
            <Typed
              strings={[fadeText]}
              typeSpeed={100}
              //backSpeed={50}
              fadeOut={true}
              fadeOutDelay={500}
              loop
            >
            </Typed> </div> : null }


      </header>
    </div>
  );
}

export default App;
