  }

  const fade = () => {

  }

  const onKeyPress = async (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      send_message();
      setInputText('');
    }

    else if(e.key == 'Space') {
      fade();
    }


  }

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInputText(e.target.value);
    //fade();
  }

  const placeholder = <Typed
  strings={[
      'Search for products',
      'Search for categories',
      'Search for brands']}
      typeSpeed={40}
      backSpeed={50}
      attr="placeholder"
      loop >

</Typed>;

  return (
    <div className="App">
      <header className="App-header">
        <InputBar text={inputText} placeholder={placeholder} onKeyPress={onKeyPress} onChange={onChange}/>

      </header>
    </div>
  );
}

export default App;
