import React, { useState } from 'react';
import './App.css';
import InputBar from './InputBar';
import Typed from 'react-typed';
import Intro from './Intro';



const App: React.FC = () => {
  const [inputText, setInputText] = useState('');
  const [showText, setShowText] = useState(true);
  const [showInput, setShowInput] = useState(false);


  const send_message = () => {

  }

  const fade = () => {
    //backspace?
  }

  const onKeyPress = async (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      if(inputText != '') {
        send_message();
        setTimeout(() =>
          {setInputText('')}, 
          500);

        setShowText(false);
        setTimeout(() =>
          {setShowText(true)}, 
          1500);
      }
    }

    else if (e.key == ' ') {
      fade();
    }


  }

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {

    setInputText(e.target.value);
  }

  const onComplete = () => {
    setTimeout(() => {
      setShowInput(true);
    }, 1000);
  }


  return (
    <div className="App">
      <div id="stars"> </div>
      <div id='stars2'></div>
      <header className="App-header">
        {showInput ? <InputBar text={inputText} placeholder="type your thoughts here" onKeyPress={onKeyPress} onChange={onChange} showText={showText}/>  : <Intro onComplete={onComplete} /> }       
      </header>
    </div>
  );
}

export default App;
