import React, { useState } from 'react';
import './App.css';
import InputBar from './InputBar';
import Typed from 'react-typed';




const App: React.FC = () => {
  const [inputText, setInputText] = useState('');
  const [fadeText, setFadeText] = useState('');
  const [showTyped, setShowTyped] = useState(false);

  const send_message = () => {

  }

  const fade = () => {

  }

  const onKeyPress = async (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      send_message();
      setInputText('');
      setFadeText('');
    }

    else if (e.key == 'Space') {
      fade();
      //setFadeText('');
      setShowTyped(false);

    }


  }

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {

    setInputText(e.target.value);
    setFadeText(e.target.value);

    //fade();
  }


  return (
    <div className="App">
      <header className="App-header">
        <InputBar autofocus={true} text={inputText} placeholder="" onKeyPress={onKeyPress} onChange={onChange} />
        <p>Press enter to release</p>

        <div id="typed-div" hidden={showTyped}>
        <Typed
          onStart={() => {}}
          onStop={() => {}}
          strings={[fadeText]}
          typeSpeed={100}
          //backSpeed={50}
          fadeOut={true} 
          loop
           >
        </Typed>
        </div>
      </header>
    </div>
  );
}

export default App;
