import styled from 'styled-components';

const InputBarStyled = styled.div.attrs({
  className: 'InputBar',
  autofocus: true,
  opacity: 0
})`
  display: flex;
  align-items: center;
  margin-top: 6rem;
  margin-bottom: -2rem;
`;


const InputText = styled.input.attrs({
  className: 'InputText',
  autofocus: true,
})`
  background-color: rgb(255, 255, 255, .05);
  border-radius: 1rem;
  border: none;
  padding: 0.25rem 2.25rem;
  font-size: 2rem;
  color: dark-grey;
  height: 4rem;
  width: 40rem;
  margin-bottom: 100px;
  color: white;

  ::placeholder {
    color: white;
    opacity: .2;
  }
  &:focus {
    outline: none !important;
  }
`;

export {
  InputBarStyled, InputText,
};
